package com.easymorse.util;import android.content.Context;import android.graphics.Bitmap;import android.util.AttributeSet;import android.widget.ImageView;import com.easymorse.util.AsyncImageLoader.ImageCallback;public class AsyncImageView extends ImageView{	public AsyncImageView(Context context) {		super(context, null);	}		public AsyncImageView(Context context, AttributeSet attrs) {		super(context, attrs);	}		public void setUrl(String url,int id){		AsyncImageLoader imageLoader = new AsyncImageLoader();		AsyncImageView.this.setImageResource(id);		imageLoader.loadDrawable(url, new ImageCallback() {			public void imageLoaded(Bitmap mBitmap, String imageUrl) {				if(mBitmap == null){									}else{					AsyncImageView.this.setImageBitmap(mBitmap);				}								}		});	}}